import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  TextField,
  Button,
  Card,
  CardContent,
  CardActions,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(4),
  },
  h1: {
    fontFamily: "Roboto",
    textAlign: "center",
    color: "#555555",
  },
  item: {
    margin: "0 1rem",
  },
}));

const NewCar = (props) => {
  const [car, setCar] = useState({});
  const classes = useStyles();

  const handelChangeName = (event) => {
    event.persist();
    setCar({ ...car, name: event.target.value });
  };

  const handelChangeSpeed = (event) => {
    event.persist();
    setCar({ ...car, topSpeed: event.target.value });
  };

  const handelChangePrice = (event) => {
    event.persist();
    setCar({ ...car, price: event.target.value });
  };

  const handleSaveData = async (event) => {
    event.preventDefault();
    const data = car;
    try {
      const res = await fetch(`${props.url}/data`, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });
      if (res.status === 200) {
        let id = 0;
        const tableData = props.data.map((car, index) => {
          id++;
          return car;
        });
        const lastCar = { _id: id + 1, ...car };
        tableData.push(lastCar);
        console.log(tableData);
        props.setData(tableData);
      }
    } catch (e) {}
  };

  return (
    <div>
      <Card variant="outlined">
        <CardContent>
          <h1 className={classes.h1}>Add a new car</h1>
          <form>
            <TextField
              id="CarName"
              label="Car name"
              variant="outlined"
              onChange={handelChangeName}
            />
            <TextField
              className={classes.item}
              id="TopSpeed"
              label="Top speed"
              variant="outlined"
              onChange={handelChangeSpeed}
            />
            <TextField
              id="Price"
              label="Price"
              variant="outlined"
              onChange={handelChangePrice}
            />
            <CardActions>
              <Button
                style={{ alignItems: "center" ,
              justifyContent: ""}}
                variant="contained"
                color="primary"
                type="submit"
                onClick={handleSaveData}>
                Add car
              </Button>
            </CardActions>
          </form>
        </CardContent>
      </Card>
    </div>
  );
};

export default NewCar;
