import React, { useState, useEffect } from "react";
import axios from "axios";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import { Button, Grid, Link } from "@material-ui/core";
import NewCar from "./NewCar";
import Editable from "./Editable";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(4),
  },
  h1: {
    fontFamily: "Roboto",
    textAlign: "center",
    color: "#555555",
  },
}));

const CarTable = (props) => {
  const classes = useStyles();

  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };

  const URI = "http://localhost:9000/api";

  const [data, setData] = useState([]);

  const handleDeleteCar = async (e, _id) => {
    try {
      const res = await axios.delete(`${URI}/data`, {
        config,
        body: {
          _id: JSON.stringify(_id),
        },
      });
      if (res.status === 200) {
        const newData = data.filter((car) => car._id !== _id);
        setData(newData);
        console.log("sucsses");
      }
    } catch (error) {
      console.log(error);
    }
  };

  const fetchData = async () => {
    try {
      const res = await fetch(`${URI}/data`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (res.status === 200) {
        const newData = await res.json();
        setData(newData);
      }
    } catch (e) {}
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Grid container spacing={10} direction="column" alignItems="center">
      <Grid item xs={12} lg={8}>
        <Grid item xs={12}>
          <h1 className={classes.h1}>Our Cars</h1>
        </Grid>
        <TableContainer component={Paper}>
          <Table className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="center">Car name</TableCell>
                <TableCell align="center">Top speed</TableCell>
                <TableCell align="center">Price</TableCell>
                <TableCell align="center">Delete</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {data.map((car) => (
                <TableRow key={car._id}>
                  <Editable name={car.name} _id={car._id}></Editable>
                  <TableCell align="left">{car.topSpeed}</TableCell>
                  <TableCell align="left">{car.price}</TableCell>
                  <TableCell align="left">
                    <Button onClick={(e) => handleDeleteCar(e, car._id)}>
                      Delete
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Grid>
    </Grid>
  );
};

export default CarTable;
