import { TableCell, TextField } from "@material-ui/core";
import axios from "axios";
import React from "react";
import Config from "../../config"

class Editable extends React.Component {
  constructor(props) {
    super(props);
    this.onClickEdit = this.onClickEdit.bind(this);
    this.onChangeValue = this.onChangeValue.bind(this);
    this.state = {
      ifClick: false,
      data: this.props.car,
      value: this.props.value,
      type: "success",
      message: "",
      open: false,
    };
  }

  onClickEdit() {
    this.setState(() => {
      return { ifClick: true };
    });
  }

  onChangeValue(event) {
    this.setState(() => {
      switch (this.props.type) {
        case "name":
          return {
            value: event.target.value,
            data: { ...this.state.data, name: event.target.value },
          };
        case "topSpeed":
          return {
            value: event.target.value,
            data: { ...this.state.data, topSpeed: event.target.value },
          };
        case "price":
          return {
            value: event.target.value,
            data: { ...this.state.data, price: event.target.value },
          };
        default:
          break;
      }
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.data !== this.state.data) {
      setTimeout(async () => {
        try {
          const res = await axios.patch(`${Config._URI}/data`, {
            config: Config._CONFIG,
            _id: {
              _id: this.props._id,
            },
            data: this.state.data,
          });
          if (res.status === 200) {
            this.setState(() => {
              return {
                 ifClick: false,
                
                 };
            });
          } else {
            
          }
        } catch (error) {}
      }, 5000);
    }
  }

  handleClose(event, reason) {
    if (reason === "clickaway") {
      return;
    }

    this.setState(() => {
      return { open: false };
    });
  }

  render() {
    return (
      <TableCell>
        {this.state.ifClick ? (
          <TextField
            autoFocus
            value={this.state.value}
            onChange={(e) => {
              this.onChangeValue(e);
            }}
            label={this.props.type}
          />
        ) : (
          <div onClick={this.onClickEdit}> {this.state.value}</div>
        )}
      </TableCell>
    );
  }
}
export default Editable;
