import React, { useState, useEffect } from "react";
import { makeStyles } from '../../../CarProjects/frontend/node_modules/@material-ui/core/styles';
import { colors
    , Grid,
    TextField,
    Button,
 } from "../../../CarProjects/frontend/node_modules/@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(4),
  },
}));

const NewCar = (props) => {

  const classes = useStyles();
  
  const [car , setCar] =  useState({})

  const handelChangeName = (event)=>{
    event.persist();
    setCar({... car, name : event.target.value})
  }

  const handelChangeSpeed = (event)=>{
    event.persist();
    setCar({... car, topSpeed : event.target.value})
  }  

  const handelChangePrice = (event)=>{
    event.persist();
    setCar({... car, price : event.target.value})
  }

  const handleSaveData = async (event)=>{
    event.preventDefault();
    const data = car;
        const res = await fetch(
          `${props.url}/data`,
          {
            method: "POST",
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(data)
          }
        );
        if(res.status===200){
            let id = 0;
            const tableData = props.data.map((car,index)=>{id++; return car;});
            const lastCar = {_id: id+1 , ... car}
            tableData.push(lastCar);
            console.log(tableData);
            props.setData(tableData);
        }
  }


  return (
    <div>
        <h1>Add a new car</h1>
        <form>
        <TextField id="CarName" label="Car name" variant="outlined" onChange = {handelChangeName} />
        <TextField id="TopSpeed" label="Top speed" variant="outlined" onChange = {handelChangeSpeed} />
        <TextField id="Price" label="Price" variant="outlined" onChange = {handelChangePrice} />
        <Button
                variant="contained"
                color="primary"
                type="submit"
                onClick={handleSaveData}
              >
                Add car
              </Button>
        </form>
    </div>
  );
}


export default NewCar;
