import React, { useState, useEffect } from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import { makeStyles } from "@material-ui/core/styles";
import { Grid,Link } from "@material-ui/core";
import NewCar from "./NewCar";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(4),
  },
  h1: {
    fontFamily: "Roboto",
    textAlign: "center",
    color: "#555555",
  },
}));

const CarTable = (props) => {
  const classes = useStyles();

  const URI = "http://localhost:9000/api";

  const [data, setData] = useState([]);

  const handleDeleteCar =async (e,_id) => {
    try {

    } catch (error) {

    }
  }

  const fetchData = async () => {
    try {
      const res = await fetch(`${URI}/data`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (res.status === 200) {
        const newData = await res.json();
        setData(newData);
      }
    } catch (e) {}
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Grid container spacing={10} direction="column" alignItems="center">
      <Grid item xs={12} lg={8}>
        <Grid item xs={12}>
          <h1 className={classes.h1}>Our Cars</h1>
        </Grid>
        <TableContainer component={Paper}>
          <Table className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell align="center">Car name</TableCell>
                <TableCell align="center">Top speed</TableCell>
                <TableCell align="center">Price</TableCell>
                <TableCell align="center">Delete</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {data.map((car) => (
                <TableRow key={car._id}>
                  <TableCell component="th" scope="row">
                    {car.name}
                  </TableCell>
                  <TableCell align="left">{car.topSpeed}</TableCell>
                  <TableCell align="left">{car.price}</TableCell>
                  <Link onClick={(e) => handleDeleteCar(e, car._id)}>Delete</Link>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Grid>
    </Grid>
  );
};

export default CarTable;
