import {TableCell, TextField} from '@material-ui/core';
import React from 'react'

class Editable extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            ifClick: false,
            data: ""
        };
       }

       render() {
           return (
                <TableCell>
                    {this.state.ifClick ? (<TextField  autoFocus value={this.props.name}  label="name"/>) : (<div> {this.props.name}</div>)}
                </TableCell>
           )
       }
}
export default Editable;
