import { TableCell, TextField } from "@material-ui/core";
import axios from "axios";
import React from "react";

const URI = "http://localhost:9000/api";
const config = {
  headers: {
    "Content-Type": "application/json",
  },
};

class Editable extends React.Component {
  constructor(props) {
    super(props);
    this.onClickEdit = this.onClickEdit.bind(this);
    this.onChangeValue = this.onChangeValue.bind(this);
    this.state = {
      ifClick: false,
      data: this.props.name,
      value: this.props.value,
    };
  }

  onClickEdit() {
    this.setState(() => {
      return { ifClick: true };
    });
  }

  onChangeValue(event) {
    console.log(event);
    this.setState(() => {
      console.log(event.target.value);
      return { data: event.target.value };
    });
  }

  componentDidUpdate(prevProps, prevState) {
    let timer;
    if (prevState.data !== this.state.data) {
      timer = setTimeout(async () => {
        try {
          const res = await axios.patch(`${URI}/data`, {
            config,
            _id: {
              _id: this.props._id,
            },
            data: {
              name: this.state.data,
            },
          });
          if (res.status === 200) {
            console.log("ok");
            this.setState(() => {
              return { ifClick: false };
            });
          } else {
            console.log("not ok");
          }
        } catch (error) {}
      }, 5000);
    }
  }

  render() {
    return (
      <TableCell>
        {this.state.ifClick ? (
          <TextField
            autoFocus
            value={this.state.value}
            onChange={(e) => {
              this.onChangeValue(e);
            }}
            label="name"
          />
        ).bind(this) : (
          <div onClick={this.onClickEdit}> {this.state.data}</div>
        )}
      </TableCell>
    );
  }
}
export default Editable;
